import React, { useState } from "react";
import { countries } from "../countries.js";
import { apiKey } from "../apikey";
import fetch from "isomorphic-unfetch";

const Index = props => {
  const [selectedCountry, setSelectedCountry] = useState("Afghanistan");
  const [confirmed, setConfirmed] = useState(0);
  const [recovered, setRecovered] = useState(0);
  const [deaths, setDeaths] = useState(0);

  async function handleChange(event, index, value) {
    setSelectedCountry(event.target.value);
    updateState(selectedCountry);
  }

  async function updateState(country) {
    let data = await fetch("https://covid-19-data.p.rapidapi.com/country", {
      method: "GET",
      headers: { "X-RapidAPI-Key": apiKey },
      params: { name: country }
    }).then(res => res.json());
    console.log(data);
  }

  fetch("https://covid-19-data.p.rapidapi.com/country", {
    method: "GET",
    headers: { "X-RapidAPI-Key": apiKey },
    params: { name: "italy" }
  })
    .then(res => res.json())
    .then(json => console.log(json));

  return (
    <div>
      <select
        onChange={handleChange}
        name="countries"
        className="countries-dropdown"
      >
        {countries.map(country => {
          return (
            <option key={country} value={country}>
              {country}
            </option>
          );
        })}
      </select>

      <p>{apiKey}</p>
    </div>
  );
};

export default Index;
